// import logo from './logo.svg';
// import './App.css';

// function App() {
//   return (
//     <div className="App">
//       <header className="App-header">
//         <img src={logo} className="App-logo" alt="logo" />
//         <p>
//           Edit <code>src/App.js</code> and save to reload.
//         </p>
//         <a
//           className="App-link"
//           href="https://reactjs.org"
//           target="_blank"
//           rel="noopener noreferrer"
//         >
//           Learn React
//         </a>
//       </header>
//     </div>
//   );
// }

// export default App;



// src/App.js
import React, { useState, useEffect } from 'react';
import { Home, Info, Utensils, DollarSign, CalendarPlus, Users, Building2, Menu, X, CheckCircle, MessageSquareWarning, ReceiptText, LogIn, LogOut, LayoutDashboard } from 'lucide-react';

// API Base URL (ensure this matches your backend port)
const API_BASE_URL = 'http://localhost:5000/api';

// Main App component
const App = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  // Authentication states
  const [user, setUser] = useState(null); // Stores user object if logged in
  const [token, setToken] = useState(null); // Stores JWT token

  // Effect to check for stored token on initial load
  useEffect(() => {
    const storedToken = localStorage.getItem('cookhubToken');
    const storedUser = localStorage.getItem('cookhubUser');
    if (storedToken && storedUser) {
      try {
        setUser(JSON.parse(storedUser));
        setToken(storedToken);
      } catch (error) {
        console.error("Failed to parse stored user data:", error);
        localStorage.removeItem('cookhubToken');
        localStorage.removeItem('cookhubUser');
      }
    }
  }, []);

  // Function to handle login
  const handleLogin = (userData, userToken) => {
    setUser(userData);
    setToken(userToken);
    localStorage.setItem('cookhubUser', JSON.stringify(userData));
    localStorage.setItem('cookhubToken', userToken);
    setCurrentPage('home'); // Redirect to home after login
  };

  // Function to handle logout
  const handleLogout = () => {
    setUser(null);
    setToken(null);
    localStorage.removeItem('cookhubUser');
    localStorage.removeItem('cookhubToken');
    setCurrentPage('home'); // Redirect to home after logout
  };

  // Function to handle navigation
  const navigate = (page) => {
    setCurrentPage(page);
    setIsMobileMenuOpen(false); // Close mobile menu on navigation
  };

  // Render the appropriate component based on the currentPage and authentication state
  const renderPage = () => {
    if (currentPage === 'auth') {
      return <AuthSection handleLogin={handleLogin} />;
    }
    if (currentPage === 'admin-dashboard' && user?.role === 'admin') {
      return <AdminDashboard token={token} />;
    }
    // For other pages, check if login is required or just render
    switch (currentPage) {
      case 'home':
        return <HomeSection />;
      case 'about':
        return <AboutSection />;
      case 'services':
        return <ServicesSection />;
      case 'pricing':
        return <PricingSection />;
      case 'booking':
        // Pass token and user data to BookingFormSection
        return <BookingFormSection token={token} user={user} />;
      case 'customer-rules':
        return <CustomerRulesSection />;
      case 'company-rules':
        return <CompanyRulesSection />;
      default:
        return <HomeSection />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 font-inter text-gray-800 antialiased flex flex-col">
      {/* Navbar component, passing user and logout handler */}
      <Navbar
        navigate={navigate}
        isMobileMenuOpen={isMobileMenuOpen}
        setIsMobileMenuOpen={setIsMobileMenuOpen}
        user={user}
        handleLogout={handleLogout}
      />

      {/* Main content area */}
      <main className="flex-grow container mx-auto px-4 py-8 sm:px-6 lg:px-8">
        {renderPage()}
      </main>

      {/* Footer */}
      <footer className="bg-gray-900 text-white py-6 mt-12 rounded-t-xl shadow-lg">
        <div className="container mx-auto text-center px-4">
          <p className="text-sm">&copy; {new Date().getFullYear()} Cookhub. All rights reserved.</p>
          <p className="text-xs mt-2">Connecting homemade cooking with heartfelt hospitality.</p>
        </div>
      </footer>
    </div>
  );
};

// Navbar Component (Updated for login/logout/admin)
const Navbar = ({ navigate, isMobileMenuOpen, setIsMobileMenuOpen, user, handleLogout }) => {
  return (
    <nav className="bg-white shadow-md py-4 rounded-b-xl">
      <div className="container mx-auto px-4 flex justify-between items-center">
        {/* Logo/Brand Name */}
        <div className="flex-shrink-0">
          <button onClick={() => navigate('home')} className="text-2xl font-bold text-green-700 hover:text-green-800 transition duration-300 ease-in-out focus:outline-none focus:ring-2 focus:ring-green-500 rounded-md p-1">
            Cookhub
          </button>
        </div>

        {/* Desktop Navigation Links */}
        <div className="hidden md:flex space-x-8 items-center">
          <NavLink icon={<Home size={18} />} text="Home" onClick={() => navigate('home')} />
          <NavLink icon={<Info size={18} />} text="About" onClick={() => navigate('about')} />
          <NavLink icon={<Utensils size={18} />} text="Services" onClick={() => navigate('services')} />
          <NavLink icon={<DollarSign size={18} />} text="Pricing" onClick={() => navigate('pricing')} />
          <NavLink icon={<CalendarPlus size={18} />} text="Book Now" onClick={() => navigate('booking')} />
          <NavLink icon={<Users size={18} />} text="Customer Rules" onClick={() => navigate('customer-rules')} />
          <NavLink icon={<Building2 size={18} />} text="Company Rules" onClick={() => navigate('company-rules')} />

          {/* Conditional rendering for Auth/Admin/Logout */}
          {user ? (
            <>
              {user.role === 'admin' && (
                <NavLink icon={<LayoutDashboard size={18} />} text="Admin" onClick={() => navigate('admin-dashboard')} />
              )}
              <button
                onClick={handleLogout}
                className="flex items-center space-x-2 text-red-600 hover:text-red-700 font-medium transition duration-300 ease-in-out px-3 py-2 rounded-lg hover:bg-red-50 focus:outline-none focus:ring-2 focus:ring-red-500"
              >
                <LogOut size={18} />
                <span>Logout ({user.name})</span>
              </button>
            </>
          ) : (
