// Booking Form Section Component (Updated to send data to backend)
const BookingFormSection = ({ token, user }) => {
  const [formData, setFormData] = useState({
    phone: '',
    address: '',
    dietaryPreference: '',
    mealPreference: '',
    planDuration: '',
    message: ''
  });
  const [statusMessage, setStatusMessage] = useState('');
  const [isSuccess, setIsSuccess] = useState(false);

  // Pre-fill name and email if user is logged in
  useEffect(() => {
    if (user) {
      // No need to pre-fill name/email as backend uses user ID
      // but ensure these fields are not required if user is logged in
    }
  }, [user]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatusMessage('');
    setIsSuccess(false);

    if (!token) {
      setStatusMessage('Please login to submit a booking request.');
      setIsSuccess(false);
      return;
    }

    try {
      const response = await fetch(`${API_BASE_URL}/bookings`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}` // Send JWT token
        },
        body: JSON.stringify(formData)
      });

      const data = await response.json();

      if (response.ok) {
        setStatusMessage(data.message || 'Booking request submitted successfully!');
        setIsSuccess(true);
        setFormData({
          phone: '',
          address: '',
          dietaryPreference: '',
          mealPreference: '',
          planDuration: '',
          message: ''
        });
      } else {
        setStatusMessage(data.message || 'Failed to submit booking request.');
        setIsSuccess(false);
      }
    } catch (error) {
      console.error('Error submitting booking:', error);
      setStatusMessage('An error occurred. Please try again.');
      setIsSuccess(false);
    }
  };

  return (
    <section className="py-16 bg-gradient-to-br from-purple-50 to-purple-100 rounded-xl shadow-lg px-8">
      <h2 className="text-4xl font-bold text-purple-800 text-center mb-10">Book Your Cookhub Experience</h2>
      <p className="text-lg text-gray-700 text-center mb-12 max-w-2xl mx-auto">
        {user ? `Welcome, ${user.name}! Fill out the form below to submit your booking request.` : 'Please login to submit a booking request.'}
      </p>

      <div className="max-w-3xl mx-auto bg-white p-8 rounded-xl shadow-md">
        {statusMessage && (
          <div className={`text-center py-4 rounded-md mb-6 ${isSuccess ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'}`}>
            {statusMessage}
          </div>
        )}

        {!user ? (
          <div className="text-center py-10">
            <LogIn size={64} className="text-purple-500 mx-auto mb-4" />
            <h3 className="text-2xl font-semibold text-gray-800 mb-2">Please Login to Book</h3>
            <p className="text-gray-600">You need to be logged in to submit a booking request. Please use the "Login/Register" link in the navigation.</p>
          </div>
        ) : (
          <form onSubmit={handleSubmit} className="space-y-6">
            <InputField label="Phone Number" type="tel" name="phone" value={formData.phone} onChange={handleChange} required />
            <InputField label="Full Address" type="text" name="address" value={formData.address} onChange={handleChange} required />

            <SelectField
              label="Dietary Preference"
              name="dietaryPreference"
              value={formData.dietaryPreference}
              onChange={handleChange}
              options={['', 'Regular Vegetarian', 'Jain', 'Swaminarayan']}
              required
            />

            <SelectField
              label="Meal Preference"
              name="mealPreference"
              value={formData.mealPreference}
              onChange={handleChange}
              options={['', 'Lunch', 'Dinner', 'Both (Contact for custom quote)']}
              required
            />

            <SelectField
              label="Preferred Plan Duration"
              name="planDuration"
              value={formData.planDuration}
              onChange={handleChange}
              options={['', '1 Month', '3 Months (Quarterly)', '12 Months (Yearly)']}
              required
            />

            <TextAreaField label="Additional Message / Specific Requirements" name="message" value={formData.message} onChange={handleChange} rows={4} />

            <button
              type="submit"
              className="w-full bg-purple-600 text-white px-6 py-3 rounded-full text-lg font-semibold shadow-md hover:bg-purple-700 transition duration-300 ease-in-out focus:outline-none focus:ring-4 focus:ring-purple-300"
            >
              Submit Booking Request
            </button>
          </form>
        )}
      </div>
    </section>
  );
};

