// Auth Section Component (Login/Register)
const AuthSection = ({ handleLogin }) => {
  const [isLogin, setIsLogin] = useState(true); // true for login, false for register
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [isError, setIsError] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    setIsError(false);

    const endpoint = isLogin ? `${API_BASE_URL}/auth/login` : `${API_BASE_URL}/auth/register`;
    const payload = isLogin ? { email, password } : { name, email, password };

    try {
      const response = await fetch(endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      const data = await response.json();

      if (response.ok) {
        setMessage(data.message);
        setIsError(false);
        if (isLogin || data.token) { // If login or successful registration with token
          handleLogin(data.user, data.token);
        }
      } else {
        setMessage(data.message || 'An error occurred.');
        setIsError(true);
      }
    } catch (error) {
      console.error('Auth error:', error);
      setMessage('Network error. Please try again.');
      setIsError(true);
    }
  };

  return (
    <section className="py-16 bg-gradient-to-br from-indigo-50 to-indigo-100 rounded-xl shadow-lg px-8">
      <h2 className="text-4xl font-bold text-indigo-800 text-center mb-10">
        {isLogin ? 'Login to Cookhub' : 'Register for Cookhub'}
      </h2>
      <p className="text-lg text-gray-700 text-center mb-12 max-w-2xl mx-auto">
        {isLogin ? 'Access your personalized services.' : 'Join Cookhub today and get access to homemade meals!'}
      </p>

      <div className="max-w-md mx-auto bg-white p-8 rounded-xl shadow-md">
        {message && (
          <div className={`text-center py-3 rounded-md mb-6 ${isError ? 'bg-red-100 text-red-700' : 'bg-green-100 text-green-700'}`}>
            {message}
          </div>
        )}
        <form onSubmit={handleSubmit} className="space-y-6">
          {!isLogin && (
            <InputField label="Full Name" type="text" name="name" value={name} onChange={(e) => setName(e.target.value)} required />
          )}
          <InputField label="Email Address" type="email" name="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
          <InputField label="Password" type="password" name="password" value={password} onChange={(e) => setPassword(e.target.value)} required />

          <button
            type="submit"
            className="w-full bg-indigo-600 text-white px-6 py-3 rounded-full text-lg font-semibold shadow-md hover:bg-indigo-700 transition duration-300 ease-in-out focus:outline-none focus:ring-4 focus:ring-indigo-300"
          >
            {isLogin ? 'Login' : 'Register'}
          </button>
        </form>
        <p className="mt-6 text-center text-gray-600">
          {isLogin ? "Don't have an account? " : "Already have an account? "}
          <button
            onClick={() => setIsLogin(!isLogin)}
            className="text-indigo-600 hover:text-indigo-800 font-semibold focus:outline-none"
          >
            {isLogin ? 'Register here' : 'Login here'}
          </button>
        </p>
      </div>
    </section>
  );
};


// Admin Dashboard Component
const AdminDashboard = ({ token }) => {
  const [users, setUsers] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError('');
      try {
        // Fetch Users
        const usersResponse = await fetch(`${API_BASE_URL}/admin/users`, {
          headers: { 'Authorization': `Bearer ${token}` }
        });
        const usersData = await usersResponse.json();
        if (usersResponse.ok) {
          setUsers(usersData);
        } else {
          throw new Error(usersData.message || 'Failed to fetch users');
        }

        // Fetch Bookings
        const bookingsResponse = await fetch(`${API_BASE_URL}/admin/bookings`, {
          headers: { 'Authorization': `Bearer ${token}` }
        });
        const bookingsData = await bookingsResponse.json();
        if (bookingsResponse.ok) {
          setBookings(bookingsData);
        } else {
          throw new Error(bookingsData.message || 'Failed to fetch bookings');
        }

      } catch (err) {
        console.error('Admin dashboard fetch error:', err);
        setError(err.message || 'Failed to load admin data.');
      } finally {
        setLoading(false);
      }
    };

    if (token) {
      fetchData();
    }
  }, [token]);

  const handleBookingStatusChange = async (bookingId, newStatus) => {
    setError('');
    try {
      const response = await fetch(`${API_BASE_URL}/admin/bookings/${bookingId}/status`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ status: newStatus })
      });

      const data = await response.json();
      if (response.ok) {
        // Update the status in the local state
        setBookings(prevBookings =>
          prevBookings.map(booking =>
            booking._id === bookingId ? { ...booking, status: newStatus } : booking
          )
        );
      } else {
        throw new Error(data.message || 'Failed to update booking status');
      }
    } catch (err) {
      console.error('Error updating booking status:', err);
      setError(err.message || 'Failed to update booking status.');
    }
  };

  if (loading) {
    return (
      <section className="py-16 text-center bg-white rounded-xl shadow-lg">
        <p className="text-xl text-gray-600">Loading admin data...</p>
      </section>
    );
  }

  if (error) {
    return (
      <section className="py-16 text-center bg-red-100 text-red-700 rounded-xl shadow-lg">
        <p className="text-xl">{error}</p>
      </section>
    );
  }

  return (
    <section className="py-16 bg-gradient-to-br from-teal-50 to-teal-100 rounded-xl shadow-lg px-8">
      <h2 className="text-4xl font-bold text-teal-800 text-center mb-10">Admin Dashboard</h2>
      <p className="text-lg text-gray-700 text-center mb-12 max-w-2xl mx-auto">
        Manage users and view all booking requests.
      </p>

      {/* Users Section */}
      <div className="mb-12 bg-white p-8 rounded-xl shadow-md">
        <h3 className="text-3xl font-bold text-teal-700 mb-6">Registered Users ({users.length})</h3>
        {users.length === 0 ? (
          <p className="text-gray-600">No users registered yet.</p>
        ) : (
          <div className="overflow-x-auto">
            <table className="min-w-full bg-white rounded-lg shadow-sm">
              <thead>
                <tr className="bg-gray-100 text-left text-gray-600 uppercase text-sm leading-normal">
                  <th className="py-3 px-6">Name</th>
                  <th className="py-3 px-6">Email</th>
                  <th className="py-3 px-6">Role</th>
                  <th className="py-3 px-6">Registered On</th>
                </tr>
              </thead>
              <tbody className="text-gray-700 text-sm font-light">
                {users.map(user => (
                  <tr key={user._id} className="border-b border-gray-200 hover:bg-gray-50">
                    <td className="py-3 px-6 text-left whitespace-nowrap">{user.name}</td>
                    <td className="py-3 px-6 text-left">{user.email}</td>
                    <td className="py-3 px-6 text-left">{user.role}</td>
                    <td className="py-3 px-6 text-left">{new Date(user.registeredAt).toLocaleDateString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* Bookings Section */}
      <div className="bg-white p-8 rounded-xl shadow-md">
        <h3 className="text-3xl font-bold text-teal-700 mb-6">All Booking Requests ({bookings.length})</h3>
        {bookings.length === 0 ? (
          <p className="text-gray-600">No booking requests submitted yet.</p>
        ) : (
          <div className="overflow-x-auto">
            <table className="min-w-full bg-white rounded-lg shadow-sm">
              <thead>
                <tr className="bg-gray-100 text-left text-gray-600 uppercase text-sm leading-normal">
                  <th className="py-3 px-6">User Name</th>
                  <th className="py-3 px-6">Email</th>
                  <th className="py-3 px-6">Phone</th>
                  <th className="py-3 px-6">Address</th>
                  <th className="py-3 px-6">Diet</th>
                  <th className="py-3 px-6">Meal</th>
                  <th className="py-3 px-6">Duration</th>
                  <th className="py-3 px-6">Message</th>
                  <th className="py-3 px-6">Status</th>
                  <th className="py-3 px-6">Requested On</th>
                  <th className="py-3 px-6">Actions</th>
                </tr>
              </thead>
              <tbody className="text-gray-700 text-sm font-light">
                {bookings.map(booking => (
                  <tr key={booking._id} className="border-b border-gray-200 hover:bg-gray-50">
                    <td className="py-3 px-6 text-left whitespace-nowrap">{booking.userName}</td>
                    <td className="py-3 px-6 text-left">{booking.userEmail}</td>
                    <td className="py-3 px-6 text-left">{booking.phone}</td>
                    <td className="py-3 px-6 text-left">{booking.address}</td>
                    <td className="py-3 px-6 text-left">{booking.dietaryPreference}</td>
                    <td className="py-3 px-6 text-left">{booking.mealPreference}</td>
                    <td className="py-3 px-6 text-left">{booking.planDuration}</td>
                    <td className="py-3 px-6 text-left max-w-xs overflow-hidden text-ellipsis">{booking.message || 'N/A'}</td>
                    <td className="py-3 px-6 text-left">
                      <span className={`px-3 py-1 rounded-full text-xs font-semibold ${
                        booking.status === 'pending' ? 'bg-yellow-200 text-yellow-800' :
                        booking.status === 'approved' ? 'bg-green-200 text-green-800' :
                        booking.status === 'demo_scheduled' ? 'bg-blue-200 text-blue-800' :
                        'bg-red-200 text-red-800'
                      }`}>
                        {booking.status}
                      </span>
                    </td>
                    <td className="py-3 px-6 text-left">{new Date(booking.createdAt).toLocaleDateString()}</td>
                    <td className="py-3 px-6 text-left">
                      <select
                        value={booking.status}
                        onChange={(e) => handleBookingStatusChange(booking._id, e.target.value)}
                        className="block w-full px-2 py-1 border border-gray-300 rounded-md shadow-sm focus:ring-teal-500 focus:border-teal-500 sm:text-sm bg-white"
                      >
                        <option value="pending">Pending</option>
                        <option value="demo_scheduled">Demo Scheduled</option>
                        <option value="approved">Approved</option>
                        <option value="rejected">Rejected</option>
                      </select>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </section>
  );
};


export default App;
