// export default App;



// src/App.js
import React, { useState, useEffect } from 'react';
import { Home, Info, Utensils, DollarSign, CalendarPlus, Users, Building2, Menu, X, CheckCircle, MessageSquareWarning, ReceiptText } from 'lucide-react';

// Main App component
const App = () => {
  // State to manage the current active page, defaulting to 'home'
  const [currentPage, setCurrentPage] = useState('home');
  // State to control the visibility of the mobile navigation menu
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  // Function to handle navigation to a different page
  const navigate = (page) => {
    setCurrentPage(page);
    setIsMobileMenuOpen(false); // Close mobile menu on navigation
  };

  // Effect hook to handle initial setup (though not fully implemented for frontend-only)
  useEffect(() => {
    console.log("Cookhub App initialized.");
  }, []);

  // Render the appropriate component based on the currentPage state
  const renderPage = () => {
    switch (currentPage) {
      case 'home':
        return <HomeSection />;
      case 'about':
        return <AboutSection />;
      case 'services':
        return <ServicesSection />;
      case 'pricing':
        return <PricingSection />;
      case 'booking':
        return <BookingFormSection />;
      case 'customer-rules':
        return <CustomerRulesSection />;
      case 'company-rules':
        return <CompanyRulesSection />;
      default:
        return <HomeSection />; // Fallback to Home
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 font-inter text-gray-800 antialiased flex flex-col">
      {/* Navbar component */}
      <Navbar navigate={navigate} isMobileMenuOpen={isMobileMenuOpen} setIsMobileMenuOpen={setIsMobileMenuOpen} />

      {/* Main content area */}
      <main className="flex-grow container mx-auto px-4 py-8 sm:px-6 lg:px-8">
        {renderPage()}
      </main>

      {/* Footer */}
      <footer className="bg-gray-900 text-white py-6 mt-12 rounded-t-xl shadow-lg">
        <div className="container mx-auto text-center px-4">
          <p className="text-sm">&copy; {new Date().getFullYear()} Cookhub. All rights reserved.</p>
          <p className="text-xs mt-2">Connecting homemade cooking with heartfelt hospitality.</p>
        </div>
      </footer>
    </div>
  );
};

// Navbar Component
const Navbar = ({ navigate, isMobileMenuOpen, setIsMobileMenuOpen }) => {
  return (
    <nav className="bg-white shadow-md py-4 rounded-b-xl">
      <div className="container mx-auto px-4 flex justify-between items-center">
        {/* Logo/Brand Name */}
        <div className="flex-shrink-0">
          <button onClick={() => navigate('home')} className="text-2xl font-bold text-green-700 hover:text-green-800 transition duration-300 ease-in-out focus:outline-none focus:ring-2 focus:ring-green-500 rounded-md p-1">
            Cookhub
          </button>
        </div>

        {/* Desktop Navigation Links */}
        <div className="hidden md:flex space-x-8">
          <NavLink icon={<Home size={18} />} text="Home" onClick={() => navigate('home')} />
          <NavLink icon={<Info size={18} />} text="About" onClick={() => navigate('about')} />
          <NavLink icon={<Utensils size={18} />} text="Services" onClick={() => navigate('services')} />
          <NavLink icon={<DollarSign size={18} />} text="Pricing" onClick={() => navigate('pricing')} />
          <NavLink icon={<CalendarPlus size={18} />} text="Book Now" onClick={() => navigate('booking')} />
          <NavLink icon={<Users size={18} />} text="Customer Rules" onClick={() => navigate('customer-rules')} />
          <NavLink icon={<Building2 size={18} />} text="Company Rules" onClick={() => navigate('company-rules')} />
        </div>

        {/* Mobile Menu Button */}
        <div className="md:hidden flex items-center">
          <button
            onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
            className="text-gray-600 hover:text-gray-900 focus:outline-none focus:ring-2 focus:ring-green-500 rounded-md p-2 transition duration-300 ease-in-out"
            aria-label="Toggle mobile menu"
          >
            {isMobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>
        </div>
      </div>

      {/* Mobile Navigation Menu */}
      {isMobileMenuOpen && (
        <div className="md:hidden bg-white border-t border-gray-200 mt-4 py-4 rounded-b-xl shadow-inner">
          <div className="flex flex-col items-center space-y-4">
            <MobileNavLink icon={<Home size={20} />} text="Home" onClick={() => navigate('home')} />
            <MobileNavLink icon={<Info size={20} />} text="About" onClick={() => navigate('about')} />
            <MobileNavLink icon={<Utensils size={20} />} text="Services" onClick={() => navigate('services')} />
            <MobileNavLink icon={<DollarSign size={20} />} text="Pricing" onClick={() => navigate('pricing')} />
            <MobileNavLink icon={<CalendarPlus size={20} />} text="Book Now" onClick={() => navigate('booking')} />
            <MobileNavLink icon={<Users size={20} />} text="Customer Rules" onClick={() => navigate('customer-rules')} />
            <MobileNavLink icon={<Building2 size={20} />} text="Company Rules" onClick={() => navigate('company-rules')} />
          </div>
        </div>
      )}
    </nav>
  );
};

// Helper component for desktop navigation links
const NavLink = ({ icon, text, onClick }) => (
  <button
    onClick={onClick}
    className="flex items-center space-x-2 text-gray-600 hover:text-green-700 font-medium transition duration-300 ease-in-out px-3 py-2 rounded-lg hover:bg-green-50 focus:outline-none focus:ring-2 focus:ring-green-500"
  >
    {icon}
    <span>{text}</span>
  </button>
);

// Helper component for mobile navigation links
const MobileNavLink = ({ icon, text, onClick }) => (
  <button
    onClick={onClick}
    className="flex items-center space-x-3 text-gray-700 hover:text-green-700 font-medium w-full justify-center py-3 rounded-lg hover:bg-green-50 focus:outline-none focus:ring-2 focus:ring-green-500 transition duration-300 ease-in-out"
  >
    {icon}
    <span>{text}</span>
  </button>
);

// Home Section Component
const HomeSection = () => (
  <section className="text-center py-16 bg-gradient-to-br from-green-50 to-green-100 rounded-xl shadow-lg">
    <h1 className="text-5xl font-extrabold text-green-800 mb-6 leading-tight">
      Cookhub: Homemade Cooking Meets <span className="text-green-600">Heartfelt Hospitality</span>
    </h1>
    <p className="text-xl text-gray-700 max-w-3xl mx-auto mb-10">
      Your trusted React.js platform connecting you with professionally trained vegetarian home cooks for healthy, hygienic, and customizable meals delivered to your doorstep.
    </p>
    <div className="flex justify-center space-x-4">
      <button className="bg-green-600 text-white px-8 py-3 rounded-full text-lg font-semibold shadow-lg hover:bg-green-700 transform hover:scale-105 transition duration-300 ease-in-out focus:outline-none focus:ring-4 focus:ring-green-300">
        Explore Services
      </button>
      <button className="bg-white text-green-700 border-2 border-green-600 px-8 py-3 rounded-full text-lg font-semibold shadow-lg hover:bg-green-50 transform hover:scale-105 transition duration-300 ease-in-out focus:outline-none focus:ring-4 focus:ring-green-300">
        View Pricing
      </button>
    </div>
    <div className="mt-12 grid grid-cols-1 md:grid-cols-3 gap-8 max-w-5xl mx-auto">
      <FeatureCard
        icon={<Utensils size={48} className="text-green-600" />}
        title="Personalized Meals"
        description="Options for Jain, Swaminarayan, and regular vegetarian diets tailored to your taste."
      />
      <FeatureCard
        icon={<Users size={48} className="text-green-600" />}
        title="Skilled Home Cooks"
        description="Certified, background-verified, and hygienically trained culinary professionals."
      />
      <FeatureCard
        icon={<CalendarPlus size={48} className="text-green-600" />}
        title="Flexible Booking"
        description="Monthly, quarterly, or yearly plans for lunch, dinner, or both."
      />
    </div>
  </section>
);

// Helper component for feature cards
const FeatureCard = ({ icon, title, description }) => (
  <div className="bg-white p-8 rounded-xl shadow-md hover:shadow-xl transition duration-300 ease-in-out transform hover:-translate-y-1 text-left">
    <div className="mb-4">{icon}</div>
    <h3 className="text-2xl font-bold text-gray-900 mb-3">{title}</h3>
    <p className="text-gray-600">{description}</p>
  </div>
);

// About Section Component
const AboutSection = () => (
  <section className="py-16 bg-white rounded-xl shadow-lg px-8">
    <h2 className="text-4xl font-bold text-green-800 text-center mb-10">About Cookhub</h2>
    <div className="max-w-4xl mx-auto text-lg text-gray-700 leading-relaxed space-y-6">
      <p>
        Cookhub is more than just a food serviceit's a movement to reconnect communities with traditional, homemade cooking.
        In today's fast-paced world, finding time to prepare healthy, hygienic, and delicious home-cooked meals can be a challenge.
        That's where Cookhub steps in.
      </p>
      <p>
        Our mission is to bring the joy and comfort of authentic homemade meals back to your daily life,
        without compromising on quality, hygiene, or your specific dietary preferences. We carefully select and train
        our home cooks, ensuring they meet the highest standards of culinary skill and cleanliness.
      </p>
      <p>
        Through our user-friendly React.js platform, we aim to provide a seamless experience from booking to meal delivery.
        We believe in transparency, respect, and building lasting relationships with both our customers and our dedicated cooks.
        Cookhub saves you time, ensures hygiene, and supports trained culinary professionals, all while offering flexible,
        healthy, and affordable food solutions.
      </p>
      <p className="font-semibold text-green-700">
        Join the Cookhub family and rediscover the taste of home!
      </p>
    </div>
  </section>
);

// Services Section Component
const ServicesSection = () => (
  <section className="py-16 bg-gradient-to-br from-blue-50 to-blue-100 rounded-xl shadow-lg px-8">
    <h2 className="text-4xl font-bold text-blue-800 text-center mb-10">Our Services</h2>
    <div className="max-w-5xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-10">
      <ServiceCard
        icon={<Utensils size={48} className="text-blue-600" />}
        title="Personalized Vegetarian Meals Only"
        description="At Cookhub, we specialize exclusively in vegetarian cuisine. We cater to diverse dietary needs including Jain, Swaminarayan, and regular vegetarian diets. Our cooks are adept at customizing meals to your specific tastes and health requirements, ensuring every dish is made just for you."
      />
      <ServiceCard
        icon={<Users size={48} className="text-blue-600" />}
        title="Skilled, Hygienically Trained Home Cooks"
        description="Our cooks are the heart of Cookhub. Each cook undergoes a rigorous selection process, including certification and background verification. They receive extensive training in food safety, hygiene protocols, and traditional cooking techniques to ensure every meal is prepared with utmost care and cleanliness."
      />
      <ServiceCard
        icon={<CalendarPlus size={48} className="text-blue-600" />}
        title="Flexible Booking Plans"
        description="We understand your dynamic lifestyle. Cookhub offers highly flexible booking options: choose lunch, dinner, or both. Our plans are available on a monthly, quarterly, or yearly basis, providing convenience and cost-effectiveness tailored to your needs."
      />
      <ServiceCard
        icon={<DollarSign size={48} className="text-blue-600" />}
        title="Transparent Pricing Packages"
