// Booking Form Section Component
const BookingFormSection = () => {
  // State for form fields
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    address: '',
    dietaryPreference: '',
    mealPreference: '',
    planDuration: '',
    message: ''
  });

  // State for showing a confirmation message
  const [showConfirmation, setShowConfirmation] = useState(false);

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  // Handle form submission (frontend only, no actual submission)
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Booking form submitted:', formData);
    // In a real app, this is where you'd send data to a backend API.
    // For this frontend-only demo, we just show a confirmation.
    setShowConfirmation(true);
    // Optionally reset form after a delay
    setTimeout(() => {
      setShowConfirmation(false);
      setFormData({
        name: '',
        email: '',
        phone: '',
        address: '',
        dietaryPreference: '',
        mealPreference: '',
        planDuration: '',
        message: ''
      });
    }, 3000); // Hide confirmation after 3 seconds
  };

  return (
    <section className="py-16 bg-gradient-to-br from-purple-50 to-purple-100 rounded-xl shadow-lg px-8">
      <h2 className="text-4xl font-bold text-purple-800 text-center mb-10">Book Your Cookhub Experience</h2>
      <p className="text-lg text-gray-700 text-center mb-12 max-w-2xl mx-auto">
        Fill out the form below, and our team will get in touch to schedule your free demo day and discuss your personalized meal plan.
      </p>

      <div className="max-w-3xl mx-auto bg-white p-8 rounded-xl shadow-md">
        {showConfirmation ? (
          <div className="text-center py-10">
            <CheckCircle size={64} className="text-green-500 mx-auto mb-4" />
            <h3 className="text-2xl font-semibold text-gray-800 mb-2">Booking Request Received!</h3>
            <p className="text-gray-600">Thank you for your interest in Cookhub. We will contact you shortly to confirm details and schedule your demo day.</p>
          </div>
        ) : (
          <form onSubmit={handleSubmit} className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <InputField label="Full Name" type="text" name="name" value={formData.name} onChange={handleChange} required />
              <InputField label="Email Address" type="email" name="email" value={formData.email} onChange={handleChange} required />
              <InputField label="Phone Number" type="tel" name="phone" value={formData.phone} onChange={handleChange} required />
              <InputField label="Full Address" type="text" name="address" value={formData.address} onChange={handleChange} required />
            </div>

            <SelectField
              label="Dietary Preference"
              name="dietaryPreference"
              value={formData.dietaryPreference}
              onChange={handleChange}
              options={['', 'Regular Vegetarian', 'Jain', 'Swaminarayan']}
              required
            />

            <SelectField
              label="Meal Preference"
              name="mealPreference"
              value={formData.mealPreference}
              onChange={handleChange}
              options={['', 'Lunch', 'Dinner', 'Both (Contact for custom quote)']}
              required
            />

            <SelectField
              label="Preferred Plan Duration"
              name="planDuration"
              value={formData.planDuration}
              onChange={handleChange}
              options={['', '1 Month', '3 Months (Quarterly)', '12 Months (Yearly)']}
              required
            />

            <TextAreaField label="Additional Message / Specific Requirements" name="message" value={formData.message} onChange={handleChange} rows={4} />

            <button
              type="submit"
              className="w-full bg-purple-600 text-white px-6 py-3 rounded-full text-lg font-semibold shadow-md hover:bg-purple-700 transition duration-300 ease-in-out focus:outline-none focus:ring-4 focus:ring-purple-300"
            >
              Submit Booking Request
            </button>
          </form>
        )}
      </div>
    </section>
  );
};

// Helper component for input fields
const InputField = ({ label, type, name, value, onChange, required }) => (
  <div>
    <label htmlFor={name} className="block text-sm font-medium text-gray-700 mb-1">
      {label} {required && <span className="text-red-500">*</span>}
    </label>
    <input
      type={type}
      id={name}
      name={name}
      value={value}
      onChange={onChange}
      required={required}
      className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-purple-500 focus:border-purple-500 sm:text-sm transition duration-200 ease-in-out"
    />
  </div>
);

// Helper component for select fields
const SelectField = ({ label, name, value, onChange, options, required }) => (
  <div>
    <label htmlFor={name} className="block text-sm font-medium text-gray-700 mb-1">
      {label} {required && <span className="text-red-500">*</span>}
    </label>
    <select
      id={name}
      name={name}
      value={value}
      onChange={onChange}
      required={required}
      className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-purple-500 focus:border-purple-500 sm:text-sm transition duration-200 ease-in-out bg-white"
    >
      {options.map((option, index) => (
        <option key={index} value={option}>{option || 'Select an option'}</option>
      ))}
    </select>
  </div>
);

// Helper component for text area fields
const TextAreaField = ({ label, name, value, onChange, rows }) => (
  <div>
    <label htmlFor={name} className="block text-sm font-medium text-gray-700 mb-1">
      {label}
    </label>
    <textarea
      id={name}
      name={name}
      value={value}
      onChange={onChange}
      rows={rows}
      className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-purple-500 focus:border-purple-500 sm:text-sm transition duration-200 ease-in-out"
    ></textarea>
  </div>
);

